public class Part2 {
    String findSimpleGene(String dna, int startCoden, int stopCoden){
        dna = dna.toUpperCase();
        dna = dna.substring(startCoden, stopCoden);
        int sCoden = dna.indexOf("ATG");
        int eCoden = dna.indexOf("TAA", sCoden);
        if(sCoden != -1){
            if(eCoden != -1){
                if((sCoden+eCoden+3) % 3 == 0){
                    return dna.substring(sCoden, eCoden+3);
                }
            }
        }
        return "";
    }
    void testSimpleGene(){
        System.out.println(findSimpleGene("",
        12, 15));
    }
    public static void main(String[] args){
        Part2 p2 = new Part2();
        p2.testSimpleGene();
    }
}
